import express from 'express'
import StatusCode from 'http-status-codes'

import userService from './services/user.service'

const appRoute1 = express.Router();

appRoute1.use(express.json());
appRoute1.use(express.urlencoded({extended:true}));

appRoute1.get('/',(req,res)=>{
    return res.send("Welcome User")
})

appRoute1.get('/users',(req,res)=>{
    const users = userService.getUsers();
    return res.status(StatusCode.OK).send({
        status: 'success',
        data: users,
        message: 'Data recieved successfully'
    });
})

appRoute1.get('/user/:id',(req,res)=>{
    const {params:{id}} = req;
    const user = userService.getUserById(parseInt(id));
    if(!user) {
        return res.status(StatusCode.OK).send({
            status: 'failure',
            message: `From route: user with id ${id} does not exist`
        });
    }
    return res.status(StatusCode.OK).send({
        status: 'success',
        data: user,
        message: 'Data recieved successfully'
    });
})

appRoute1.post('/add', (req,res)=>{
    const {body:user} = req;

    if(!user.name) {
        return res.status(StatusCode.BAD_REQUEST).send({
            status: 'failure',
            message: 'Name is required'
        });
    }
    const addedUser = userService.addUser(user);
    return res.status(StatusCode.ACCEPTED).send({
        status: 'success',
        data: addedUser,
        message: 'Data recieved successfully'
    });
})

appRoute1.put('/update/:id', (req,res)=>{
    const {body:user} = req;

    const {id} = req.params;
    const updatedUser = userService.updateUser(parseInt(id),user);
    if(updatedUser) {
        return res.status(StatusCode.ACCEPTED).send({
            status: 'success',
            data: updatedUser,
            message: 'Data updated successfully'
        });
    }
    else {
        return res.status(StatusCode.NOT_FOUND).send({
            status: 'failure',
            data: {},
            message: `User with ID ${id} not found`
        });
    }
})

export default appRoute1;

